import React, { useState } from 'react';
import { useTheme } from '../context/ThemeContext';
import './css/Services.css';

const services = [
  { name: 'Teamwork', image: '/path/to/1.png' },
  { name: 'Design', image: '/path/to/2.png' },
  { name: '3D Animation', image: '/path/to/3.png' },
  { name: 'Java Developer', image: '/path/to/4.png' },
];

const Services = () => {
  const { theme } = useTheme();
  const [hoveredService, setHoveredService] = useState(null);

  return (
    <div className={`services-section ${theme}`}>
      <div className="top-line"></div>
      <h2 className="services-title">SERVICES</h2>
      <div className="services-list">
        {services.map((service, index) => (
          <div
            key={index}
            className="service-item"
            onMouseEnter={() => setHoveredService(service.image)}
            onMouseLeave={() => setHoveredService(null)}
          >
            {service.name}
            {hoveredService === service.image && (
              <img src={service.image} alt={service.name} className="service-image" />
            )}
          </div>
        ))}
      </div>
      <div className="bottom-line"></div>
    </div>
  );
};

export default Services;
