import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { UserProvider } from './context/UserContext';
import { ThemeProvider } from './context/ThemeContext';
import { LanguageProvider } from './context/LanguageContext';
import { StoreProvider } from './redux/store'; // Путь к вашему store
import Header from './components/Header';
import Profile from './components/Profile';
import PostList from './components/PostList';
import PostForm from './components/PostForm';
import Login from './components/Login';
import Register from './components/Register';
import Footer from './components/Footer';
import AnimatedText from './components/AnimatedText';
import About from './components/About';
import Services from './components/Services';
import AddPost from './components/AddPost'; // Импорт компонента AddPost
import EditPost from './components/EditPost'; // Импорт компонента EditPost

const Home = () => (
  <>
    <Header />
    <main>
      <AnimatedText />
      <About />
      <Services />
    </main>
    <Footer />
  </>
);

const PostsPage = () => (
  <>
    <Header />
    <main>
      <PostList />
    </main>
    <Footer />
  </>
);

const App = () => {
  return (
    <StoreProvider>
      <UserProvider>
        <ThemeProvider>
          <LanguageProvider>
            <Router>
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/add-post" element={<AddPost />} /> {/* Используем AddPost для добавления */}
                <Route path="/edit-post/:postId" element={<EditPost />} /> {/* Используем EditPost для редактирования */}
                <Route path="/posts" element={<PostsPage />} />
                <Route path="/" element={<Home />} />
              </Routes>
            </Router>
          </LanguageProvider>
        </ThemeProvider>
      </UserProvider>
    </StoreProvider>
  );
};

export default App;
