import React from 'react';
import { useTheme } from '../context/ThemeContext';
import './css/AnimatedText.css';

const AnimatedText = () => {
  const { theme } = useTheme();

  return (
    <div className={`animated-text ${theme}`}>
      <div className="text-container">
        <h1 className="animated-header">ASSEM - KASSENOVA</h1>
        <div className="line"></div>
        <p className="description">
          WE ARE A FULL-SERVICE CREATIVE STUDIO CREATING AWESOME DIGITAL EXPERIENCES AND SOLUTIONS. OUR MISSION IS TO MAKE WORK PROCESS MEANINGFUL.
        </p>
        <div className="social-icons">
          <div className="icon instagram">IG</div>
          <div className="icon twitter">TW</div>
          <div className="icon facebook">FB</div>
        </div>
      </div>
      <div className="image-container">
        <img src="/path/to/your/image1.png" alt="Profile" className="circle-image profile-image" />
        <img src="/path/to/your/image2.png" alt="Project" className="circle-image project-image" />
      </div>
    </div>
  );
};

export default AnimatedText;
