import React, { useState } from 'react';
import './css/About.css';

const About = () => {
  const [activeSection, setActiveSection] = useState(null);

  const toggleSection = (section) => {
    if (activeSection === section) {
      setActiveSection(null);
    } else {
      setActiveSection(section);
    }
  };

  return (
    <div className="about">
      <div className="about-section">
        <button onClick={() => toggleSection('aboutMe')} className="about-button">
          ABOUT ME {activeSection === 'aboutMe' ? '-' : '+'}
        </button>
        {activeSection === 'aboutMe' && (
          <div className="about-content">
            <p>This is a placeholder text about me. You can update this text with your personal information.</p>
          </div>
        )}
      </div>

      <div className="about-section">
        <button onClick={() => toggleSection('myGoals')} className="about-button">
          MY GOALS {activeSection === 'myGoals' ? '-' : '+'}
        </button>
        {activeSection === 'myGoals' && (
          <div className="about-content">
            <p>This is a placeholder text about my goals. You can update this text with your personal goals.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default About;
