import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTheme } from '../context/ThemeContext';
import { useLanguage } from '../context/LanguageContext';
import { useUser } from '../context/UserContext';
import { ReactComponent as SunIcon } from '../assets/sun.svg';
import { ReactComponent as MoonIcon } from '../assets/moon.svg';
import './css/Header.css';

const Header = () => {
  const { theme, toggleTheme } = useTheme();
  const { language, toggleLanguage, translations } = useLanguage();
  const { user, setUser } = useUser();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <header className={`header ${theme}`}>
      <div className="header-content">
        <div className="header-left">
          <div className="header-title">
            <div>{translations[language].name}</div>
            <div className='kassenova'>{translations[language].surname}</div>
          </div>
          <div className="portfolio">{translations[language].portfolio}</div>
        </div>
        <div className="header-right">
          <nav>
            <Link to="/projects">{translations[language].projects}</Link>
            <Link to="/about">{translations[language].about}</Link>
            <Link to="/services">{translations[language].services}</Link>
            <Link to="/animated-text">{translations[language].animatedText}</Link>
            <select value={language} onChange={(e) => toggleLanguage(e.target.value)}>
              <option value="en">EN</option>
              <option value="ru">RU</option>
              <option value="kk">KK</option>
            </select>
            <button onClick={toggleTheme} className="theme-toggle">
              {theme === 'light' ? <SunIcon /> : <MoonIcon />}
            </button>
            {user ? (
              <button onClick={handleLogout}>{translations[language].logout}</button>
            ) : (
              <>
                <Link to="/login">{translations[language].login}</Link>
                <Link to="/register">{translations[language].register}</Link>
              </>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
