import React, { useState } from 'react';
import { useTheme } from '../context/ThemeContext';
import './css/About.css';

const About = () => {
  const { theme } = useTheme();
  const [isStoryOpen, setIsStoryOpen] = useState(false);
  const [isMissionOpen, setIsMissionOpen] = useState(false);

  const toggleStory = () => setIsStoryOpen(!isStoryOpen);
  const toggleMission = () => setIsMissionOpen(!isMissionOpen);

  return (
    <div className={`about-section ${theme}`}>
      <h2>About Me</h2>
      <p className="about-description">Welcome to the profile of Assem Kassenova</p>
      <div className="accordion">
        <div className="accordion-item">
          <div className="accordion-title" onClick={toggleStory}>
            <span>My Story</span>
            <span>{isStoryOpen ? '-' : '+'}</span>
          </div>
          {isStoryOpen && (
            <div className="accordion-content">
              <p>I am an undergraduate student majoring in "Digital Management and Design" at Narxoz University, Almaty. As part of my studies, I am actively learning how technologies can transform management and design, making processes more efficient and user-oriented.</p>
            </div>
          )}
        </div>
        <div className="accordion-item">
          <div className="accordion-title" onClick={toggleMission}>
            <span>My Mission</span>
            <span>{isMissionOpen ? '-' : '+'}</span>
          </div>
          {isMissionOpen && (
            <div className="accordion-content">
              <p>I aspire to take on the role of a Project Manager, where I can apply my skills in technology, project management, and teamwork to successfully complete projects. I aim to lead projects that not only meet technical requirements but also exceed client expectations in terms of quality and innovation.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default About;
