import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './css/PostList.css';
import { useTheme } from '../context/ThemeContext';

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedPost, setSelectedPost] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const { theme } = useTheme();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:4444/posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Failed to fetch posts', error);
      }
    };

    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:4444/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Failed to fetch categories', error);
      }
    };

    const checkAuth = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        try {
          await axios.get('http://localhost:4444/auth/me', {
            headers: { Authorization: `Bearer ${token}` },
          });
          setIsAuthenticated(true);
        } catch (error) {
          setIsAuthenticated(false);
        }
      }
    };

    fetchPosts();
    fetchCategories();
    checkAuth();
  }, []);

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  const handleBackClick = () => {
    setSelectedPost(null);
  };

  const handleEdit = (postId) => {
    // Logic for editing a post
  };

  const handleDelete = async (postId) => {
    try {
      await axios.delete(`http://localhost:4444/posts/${postId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setPosts(posts.filter(post => post._id !== postId));
      setSelectedPost(null);
    } catch (error) {
      console.error('Failed to delete post', error);
    }
  };

  const filteredPosts = selectedCategory
    ? posts.filter(post => post.category.name === selectedCategory)
    : posts;

  if (selectedPost) {
    return (
      <div className={`post-details ${theme}`}>
        <button onClick={handleBackClick}>Back</button>
        <h2>{selectedPost.title}</h2>
        {selectedPost.imageUrl && (
          <img src={`http://localhost:4444${selectedPost.imageUrl}`} alt={selectedPost.title} />
        )}
        <p>{selectedPost.text}</p>
        <p><strong>Category:</strong> {selectedPost.category.name}</p>
        <p><strong>Tags:</strong> {selectedPost.tags.join(', ')}</p>
        <p><strong>Author:</strong> {selectedPost.user.fullName}</p>
        {isAuthenticated && (
          <div className="post-actions">
            <button onClick={() => handleEdit(selectedPost._id)}>Edit</button>
            <button onClick={() => handleDelete(selectedPost._id)}>Delete</button>
          </div>
        )}
      </div>
    );
  }

  return (
    <div className={`post-list ${theme}`}>
      <div className="filter">
        <label htmlFor="category">Filter by category: </label>
        <select
          id="category"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">All</option>
          {categories.map((category) => (
            <option key={category._id} value={category.name}>
              {category.name}
            </option>
          ))}
        </select>
      </div>
      {filteredPosts.map((post) => (
        <div key={post._id} className="post-item" onClick={() => handlePostClick(post)}>
          <h3>{post.title}</h3>
          {post.imageUrl && (
            <img src={`http://localhost:4444${post.imageUrl}`} alt={post.title} />
          )}
          <p>{post.text.slice(0, 100)}...</p>
        </div>
      ))}
    </div>
  );
};

export default PostList;
