import React, { createContext, useContext, useState, useEffect } from 'react';

const LanguageContext = createContext();

const translations = {
  en: {
    welcome: "Welcome to the homepage!",
    projects: "Projects",
    switchTheme: "Switch Theme",
    addPost: "Add Post",
    savePost: "Save Post",
    delete: "Delete",
    edit: "Edit",
    title: "Title",
    text: "Text",
    tags: "Tags",
    category: "Category",
    uploadImage: "Upload Image",
    selectCategory: "Select Category",
  },
  ru: {
    welcome: "Добро пожаловать на главную страницу!",
    projects: "Проекты",
    switchTheme: "Сменить тему",
    addPost: "Добавить пост",
    savePost: "Сохранить пост",
    delete: "Удалить",
    edit: "Редактировать",
    title: "Название",
    text: "Текст",
    tags: "Теги",
    category: "Категория",
    uploadImage: "Загрузить изображение",
    selectCategory: "Выбрать категорию",
  },
  kk: {
    welcome: "Басты бетке қош келдіңіз!",
    projects: "Жобалар",
    switchTheme: "Тақырыпты ауыстыру",
    addPost: "Жазба қосу",
    savePost: "Жазбаны сақтау",
    delete: "Жою",
    edit: "Өңдеу",
    title: "Тақырып",
    text: "Мәтін",
    tags: "Тегтер",
    category: "Санат",
    uploadImage: "Сурет жүктеу",
    selectCategory: "Санатты таңдау",
  }
};

export const LanguageProvider = ({ children }) => {
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const savedLanguage = localStorage.getItem('language');
    if (savedLanguage) {
      setLanguage(savedLanguage);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('language', language);
  }, [language]);

  const toggleLanguage = (lang) => {
    setLanguage(lang);
  };

  return (
    <LanguageContext.Provider value={{ language, toggleLanguage, translations }}>
      {children}
    </LanguageContext.Provider>
  );
};

export const useLanguage = () => useContext(LanguageContext);
