import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PostForm = ({ postId, initialData, onSuccess }) => {
  const [title, setTitle] = useState(initialData?.title || '');
  const [text, setText] = useState(initialData?.text || '');
  const [tags, setTags] = useState(initialData?.tags?.join(', ') || '');
  const [category, setCategory] = useState(initialData?.category || '');
  const [imageFile, setImageFile] = useState(null);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    async function fetchCategories() {
      try {
        const response = await axios.get('http://localhost:4444/categories');
        setCategories(response.data);
      } catch (error) {
        console.error('Failed to fetch categories', error);
      }
    }

    fetchCategories();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('text', text);
    formData.append('tags', tags);
    formData.append('category', category);
    if (imageFile) {
      formData.append('image', imageFile);
    }

    try {
      const config = {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data'
        },
      };

      if (postId) {
        await axios.patch(`http://localhost:4444/posts/${postId}`, formData, config);
      } else {
        await axios.post('http://localhost:4444/posts', formData, config);
      }
      alert('Post saved successfully');
      if (onSuccess) {
        onSuccess();
      }
    } catch (error) {
      console.error('Failed to save post', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div>
        <label>Text</label>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </div>
      <div>
        <label>Tags</label>
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />
      </div>
      <div>
        <label>Category</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat._id} value={cat.name}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Upload Image</label>
        <input
          type="file"
          onChange={(e) => setImageFile(e.target.files[0])}
        />
      </div>
      <button type="submit">Save Post</button>
    </form>
  );
};

export default PostForm;
