import React from 'react';
import { Link } from 'react-router-dom';
import { useTheme } from '../../context/ThemeContext';
import { useLanguage } from '../../context/LanguageContext';
import { ReactComponent as SunIcon } from '../../assets/sun.svg';
import { ReactComponent as MoonIcon } from '../../assets/moon.svg';
import './css/Header.css';

const Header = () => {
  const { theme, toggleTheme } = useTheme();
  const { language, toggleLanguage, translations } = useLanguage();
  const logoSrc = theme === 'light' ? '/black-logo.svg' : '/white-logo.svg';

  return (
    <header className="header">
      <div className="header-content">
        <div className="header-left">
          <img src={logoSrc} alt="Assem K. Logo" className="logo" />
        </div>
        <div className="header-right">
          <nav>
            <Link to="/projects">{translations[language].projects}</Link>
            <select value={language} onChange={(e) => toggleLanguage(e.target.value)}>
              <option value="en">EN</option>
              <option value="ru">RU</option>
              <option value="kk">KK</option>
            </select>
            <button onClick={toggleTheme} className="theme-toggle">
              {theme === 'light' ? <SunIcon /> : <MoonIcon />}
            </button>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
