import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const PostForm = () => {
  const [title, setTitle] = useState('');
  const [text, setText] = useState('');
  const [category, setCategory] = useState('');
  const [tags, setTags] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const navigate = useNavigate();
  const { postId } = useParams();

  useEffect(() => {
    if (postId) {
      // Fetch the post data for editing
      const fetchPost = async () => {
        try {
          const response = await axios.get(`http://localhost:4444/posts/${postId}`);
          const post = response.data;
          setTitle(post.title);
          setText(post.text);
          setCategory(post.category._id);
          setTags(post.tags.join(', '));
          setImageUrl(post.imageUrl);
        } catch (error) {
          console.error('Failed to fetch post', error);
        }
      };
      fetchPost();
    }
  }, [postId]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const postData = {
      title,
      text,
      category,
      tags: tags.split(',').map(tag => tag.trim()),
      imageUrl,
    };

    try {
      if (postId) {
        // Update the existing post
        await axios.put(`http://localhost:4444/posts/${postId}`, postData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
      } else {
        // Create a new post
        await axios.post('http://localhost:4444/posts', postData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
      }
      navigate('/posts');
    } catch (error) {
      console.error('Failed to submit post', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Text:</label>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Category:</label>
        <input
          type="text"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Tags:</label>
        <input
          type="text"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Image URL:</label>
        <input
          type="text"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)}
        />
      </div>
      <button type="submit">{postId ? 'Update Post' : 'Create Post'}</button>
    </form>
  );
};

export default PostForm;
